namespace app\Services;

use App\Classes\IMrtCrawler;
use App\Library\GoogleGeocoding;
use App\Models\Mrt;
use App\Repositories\MrtRepository;

class MrtService
{
    protected $mrtCrawler;
    protected $geoLib;
    protected $mrt;

    /**
     * MrtService constructor.
     */
    public function __construct(IMrtCrawler $mrtCrawler , MrtRepository $mrt)
    {
        $this->mrtCrawler = $mrtCrawler;
        $this->geoLib = new GoogleGeocoding();
        $this->mrt = $mrt;
    }

    public function getLocationWithGeo()
    {
        $mrtArea = "台北";
        set_time_limit(900);

        //爬蟲取得台北捷運地址
        $stations = $this->mrtCrawler->startCraw();

        $geoLocation = $stations;

        //將地址丟給Google Api轉換為座標
        foreach ($stations as $index => $stop)
        {
            $location = $this->geoLib->addressToGeocode($stop["address"]);

            //只要有一次座標取得失敗、就整段不做
            if ($location === false)
            {
                return false;
            }
            $geoLocation[$index]["lat"] = $location["lat"];
            $geoLocation[$index]["lng"] = $location["lng"];
        }

//        var_export($geoLocation);
//        echo "</PRE>";
//        $geoLocation = $this->getDummyLocation();

        $mrt = $this->mrt->getModel();

        foreach($geoLocation as $singleLocation)
        {
            $attr = [
                "uid" => $singleLocation["id"],
                "area" => $mrtArea,
            ];

            $data = [
                "uid" => $singleLocation["id"],
                "line" => $singleLocation["line"],
                "name" => $singleLocation["name"],
                "address" => $singleLocation["address"],
                "area" => $mrtArea,
                "lat" => $singleLocation["lat"],
                "lng" => $singleLocation["lng"],
            ];
            $mrt::updateOrCreate($attr,$data);
        }

        return true;
    }

